namespace MUnique.OpenMU.Network.Tests
{
    using System;
    using MUnique.OpenMU.Network;
    using NUnit.Framework;

    /// <summary>
    /// This test tests the default game server encryptor and decryptor classes.
    /// </summary>
    [TestFixture]
    public class EncryptionTest
    {
        /// <summary>
        /// Tests the decryption of C1-packets with the <see cref="Decryptor"/>.
        /// </summary>
        [Test]
        public void C1Decrypt()
        {
            var expected = Convert.FromBase64String("");
            var packet = Convert.FromBase64String("");
            var decryptor = new Decryptor();
            var success = decryptor.Decrypt(ref packet);
            Assert.That(success, Is.True);
            CompareArrays(expected, packet);
        }

        /// <summary>
        /// Tests the decryption of C2-packets with the <see cref="Decryptor"/>.
        /// </summary>
        [Test]
        public void C2Decrypt()
        {
            var expected = Convert.FromBase64String("");
            var packet = Convert.FromBase64String("");
            var decryptor = new Decryptor();
            var success = decryptor.Decrypt(ref packet);
            Assert.That(success, Is.True);
            CompareArrays(expected, packet);
        }

        /// <summary>
        /// Tests the C1-Packet encryption. C1 packet to the client are not getting encrypted, so we check this.
        /// </summary>
        [Test]
        public void C1Encrypt()
        {
            var packet = new byte[] { 0xC1, 0x05, 0xAB, 0xBC, 0xDE };
            var encryptor = new Encryptor();
            var result = encryptor.Encrypt(packet);
            CompareArrays(packet, result);
        }

        /// <summary>
        /// Tests the C2-Packet encryption. C2 packet to the client are not getting encrypted, so we check this.
        /// </summary>
        [Test]
        public void C2Encrypt()
        {
            var packet = new byte[] { 0xC2, 0x00, 0x05, 0xBC, 0xDE };
            var encryptor = new Encryptor();
            var result = encryptor.Encrypt(packet);
            CompareArrays(packet, result);
        }

        /// <summary>
        /// Tests the encryption of C3-packets with the <see cref="Encryptor"/>.
        /// </summary>
        [Test]
        public void C3Encrypt()
        {
            var packet = Convert.FromBase64String("w7kxFgK8hYpGGLgdXe7ZpTZViB+r3sRI3YSqZs7/Mh5Vmh2mXqs+3dqkvURmXrL57ASs+FkJz/236Tl9ER67R+WZyMLRMkeLF6tEBiB/4X7SsXrKUznES8of73RxwMy76HZezJbvJ7m9IOGuxcjcNwe6q1+k8fOs1Hz3sULSGlbfiB6qIBXo4onADTNYFoYCQrdtthVsF/aDsvcZ93V36gaKzzyqMhby0sjV4+TAU7719W6LZWNAcnA=");
            var expected = Convert.FromBase64String("");

            var encryptor = new Encryptor();
            var result = encryptor.Encrypt(packet);

            CompareArrays(expected, result);
        }

        /// <summary>
        /// Tests the encryption of C4-packets with the <see cref="Encryptor"/>.
        /// </summary>
        [Test]
        public void C4Encrypt()
        {
            var packet = Convert.FromBase64String("");
            var expected = Convert.FromBase64String("");

            var encryptor = new Encryptor();
            var result = encryptor.Encrypt(packet);

            CompareArrays(expected, result);
        }

        /// <summary>
        /// Tests the decryption of C4-packets with the <see cref="Decryptor"/>.
        /// </summary>
        [Test]
        public void C4Decrypt()
        {
            var decryptedPacket = Convert.FromBase64String("");
            var encryptedPacket = Convert.FromBase64String("");

            var decryptor = new Decryptor();
            var success = decryptor.Decrypt(ref encryptedPacket);

            Assert.That(success, Is.True);
            CompareArrays(encryptedPacket, decryptedPacket);
        }

        /// <summary>
        /// Tests the decryption of C3-packets with the <see cref="Decryptor"/>.
        /// </summary>
        [Test]
        public void C3Decrypt()
        {
            var decryptedPacket = Convert.FromBase64String("w7gAudHEjjSP53H6Rkp3oXj7B9z+rVDR2f0Is4bvsIsUL3RM/aTDB2FX9YG3Hkboy1Z1JThot558MeDTvNuunzfl5RbWK6TTOP97prjPGbq3IOcweopTq3fVz8vD8EuFqVVJ0jgvEZ+xoe047RHmrRgmG5zzfSWtkTmeAVzZD0i09f1jhUeBiA5HfticGr5m7iGzndSvkSwvm0D/kRBD15GlhPgTgyfQpJONrP5NEHd7NxI6JnJzBQ==");
            var encryptedPacket = Convert.FromBase64String("");

            var decryptor = new Decryptor();
            var success = decryptor.Decrypt(ref encryptedPacket);

            Assert.That(success, Is.True);
            CompareArrays(encryptedPacket, decryptedPacket);
        }

        private static void CompareArrays(byte[] expected, byte[] actual)
        {
            Assert.That(actual.Length, Is.EqualTo(expected.Length));
            for (int i = 0; i < actual.Length; i++)
            {
                Assert.That(actual[i], Is.EqualTo(expected[i]), "index {0}", i);
            }
        }
    }
}
